import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

final Logger logger = LoggerFactory.getLogger("organisation.create.logger");

org_admin_role_id = "org_admin_role_" + newObject._id;

logger.info("Create an associated organisation admin role: {}", org_admin_role_id)

org_admin_role = [
  "name": org_admin_role_id,
  "_id": org_admin_role_id,
  "description": "Organisation admin role for organisation '" + newObject.corporateName + "'"]

role = openidm.create("managed/role", org_admin_role_id, org_admin_role);

logger.info("Created an associated organisation admin role: {}", org_admin_role)

