import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

final Logger logger = LoggerFactory.getLogger("organisation.create.logger");

org_admin_role_id = "org_admin_role_" + oldObject._id
logger.info("Delete the associated organisation admin role: {}", org_admin_role_id)
try {
  openidm.delete("managed/role/" + org_admin_role_id, null);
} catch(e) {
  logger.error("Could not delete associated role '{}' while deleting organisation '{}'", 
               org_admin_role_id, 
               oldObject._id,
               e)
}
